mapper.insert:
INSERT INTO TBL_MEMBER
(
  MEMBER_CODE
, MEMBER_NAME
, PHONE
, EMAIL
, MEMBER_ID
, MEMBER_PWD
, MILEAGE
, POLICY_YN
, PRIVATE_YN
, SMS_YN
, EMAIL_YN
, GRADE_CODE
, LOGIN_TYPE
, TEL
, JOIN_DAY
, MEMBER_STATUS
)
VALUES
    (
      SEQ_MEMBER_CODE.NEXTVAL
    , #{ memberName }
    , #{ phone }
    , #{ email }
    , #{ memberId }
    , #{ memberPwd }
    , #{ mileage }
    , #{ policyYn }
    , #{ privateYn }
    , #{ smsYn }
    , #{ emailYn }
    , #{ gradeCode }
    , #{ loginType }
    , #{ tel }
    , #{ joinDay }
    , #{ memberStatus }
    )

INSERT INTO TBL_ADDRESS
(
  ADDRESS_CODE
, ADDRESS_NAME
, POSTAL_CODE
, MEMBER_CODE
, DEFAULT_ADR
, OPTIONAL_ADR
, DEFAULT_YN
)
VALUES
    (
      SEQ_ADDRESS_CODE.NEXTVAL
    , #{ addressName }
    , #{ postalCode }
    , SEQ_MEMBER_CODE.CURRVAL
    , #{ defaultAdr }
    , #{ optionalAdr }
    , #{ defaultYn }
    )

mapper.select:
SELECT MEMBER_ID
  FROM TBL_MEMBER
 WHERE MEMBER_NAME = #{ memberName }
   AND (PHONE = #{ phone }
    OR EMAIL = #{ email })
