mapper.select:
SELECT A.NOTICE_CODE , A.NOTICE_TITLE , A.REGISTERED_DATE FROM (SELECT ROWNUM RNUM, B.NOTICE_CODE , B.NOTICE_TITLE , B.REGISTERED_DATE FROM (SELECT C.NOTICE_CODE , C.NOTICE_TITLE , C.REGISTERED_DATE FROM TBL_NOTICE C
NOTICE_TITLE LIKE '%' || #{searchValue} || '%' OR NOTICE_BODY LIKE '%' || #{searchValue} || '%'
NOTICE_TITLE LIKE '%' || #{searchValue} || '%'
NOTICE_BODY LIKE '%' || #{searchValue} || '%'
AND STATUS_YN = 'Y' ORDER BY NOTICE_CODE desc
) B
WHERE ROWNUM <= #{ endRow }
) A WHERE A.RNUM >= #{ startRow } ORDER BY 1 DESC
